import React from 'react';

import './Board.css';
import {Cell} from '../cell/Cell';

export const Board = () => {
	const cellValues =['X','X','X','X','X','O','O','O','O'];
	const winningCombination = [0,1,2];

	const cells = cellValues.map((value, index) => {
		const canHighlight = winningCombination && winningCombination.indexOf(index) >= 0;

		return <Cell 
					key={index} value={value} canHighlight ={false}/>
	});
  return (<>
      	<div id="board">
			{cells}
      	</div>
  </>
  );
}
